/**
 * Customer address controller
 *
 * @category   Mage
 * @package    Mage_Customer
 * @author      Magento Core Team <core@magentocommerce.com>
 */
class Mage_Customer_EmailController extends Mage_Core_Controller_Front_Action
{
    /**
     * Retrieve customer session object
     *
     * @return Mage_Customer_Model_Session
     */
    protected function _getSession()
    {
        return Mage::getSingleton('customer/session');
    }

    public function preDispatch()
    {
        parent::preDispatch();

        if (!Mage::getSingleton('customer/session')->authenticate($this)) {
            $this->setFlag('', 'no-dispatch', true);
        }
    }
 public function getemailAction()
        {
                        $Id= Mage::getSingleton('customer/session')->getCustomer()->getId();
                        $write_old = Mage::getSingleton('core/resource')->getConnection('core_write');
                        $select="SELECT * FROM `customer_entity where entity_id='".$Id."'";
                        $row = $write_old->fetchRow($select);
                        if ($row['email']!='')
                        {
                                $result=$row['email'];
                        }
                        else
                        {
                                $result='No email';
                        }
                print_r($result);
        }


}
